(()=>{"use strict";fetch("https://api.weatherapi.com/v1/current.json?key=b677082ac19f45aca07212645220401&q=Kiev&aqi=no").then((t=>t.json())).then((t=>{!function(t){document.getElementById("app").innerHTML=`\n    Here is your daily weather!\n    <div> City: ${t.location.name}; </div>\n    <div> Country: ${t.location.country}; </div>\n    <div> Current temperature: ${t.current.temp_c};</div>\n    <div> Humidity:${t.current.humidity}; </div>\n    <div>${t.current.condition.text};</div>\n    <div>\n      <img src="http:////cdn.weatherapi.com/weather/64x64/day/116.png"" alt="">\n    </div>\n    `}(t)}))})();